mapper.select:
SELECT A.*,B.* FROM ( SELECT TT1.rnum AS no, TT1.seq, TT1.div, TT1.title, TT1.read_cnt, DECODE(TO_CHAR(SYSDATE,'YYYY/MM/DD'),TO_CHAR(TT1.mod_dt,'YYYY/MM/DD') , TO_CHAR(TT1.mod_dt,'HH24:MI') , TO_CHAR(TT1.mod_dt,'YYYY/MM/DD')) mod_dt, TT1.mod_id FROM ( SELECT ROWNUM rnum, T1.* FROM ( SELECT * FROM board WHERE div = #{div}
ORDER BY mod_dt DESC )T1 WHERE ROWNUM <=( #{pageSize} * (#{pageNo} - 1)+ #{pageSize} ) ) TT1 WHERE rnum >=( #{pageSize} * (#{pageNo} - 1)+ 1 )
)A CROSS JOIN( SELECT COUNT(*) total_cnt FROM board WHERE div = #{div}
)B
